import React, {useEffect, useState} from 'react';
import {Helmet} from 'react-helmet';

function Ingredient() {
    useEffect( () => {
        fetchItems();
    }, []);

    const [items, setItems] = useState([]);

    const fetchItems = async () => {
        const data = await fetch('/ingredients');
        const items = await data.json();
        setItems(items);
    };

    return(


        <section>
            <div
      style={{
        background: "linear-gradient(#87DED7, #9198e5)", 
        width: '6000px',
        height: '1000px'
      }}
    />

            <div class="container-fluid">


            <h1><span style={{color : 'darkcyan'}}>Ingredients</span></h1>
                <form method="POST" action="/addIngredient">
                    <div class="input-group justify-content-center">



                        <div className="recipeIngredients"> 
                            <input type="text" 
                                placeholder="Input ingredients..."
                                autoComplete='Off'
                                class="form-control" />

                            </div>













                        </div>

                        <div classname = "button">

                           <input type="submit" value="Send" class="btn btn-primary mb-2" />

                        </div>



                </form>



                {
                items.map(item => (
                    <div class="row padding">
                        <div class="alert alert-info rounded-pill" role="alert">
                            <i class="fa fa-user mr-2"></i> <i> Ingredient: {item.ingredients} </i>
                        </div>
                    </div>       
                ))
                }


            </div>
        </section>
    );
}

export default Ingredient;
