import React, {useEffect, useState} from 'react';

function Ingredient() {
    useEffect( () => {
        fetchItems();
    }, []);

    const [items, setItems] = useState([]);

    const fetchItems = async () => {
        const data = await fetch('/ingredients');
        const items = await data.json();
        setItems(items);
    };

    return(
        <section>

            <div class="container-fluid">
                <h1 class="mt-5">Ingredients</h1>
                <form method="POST" action="/addIngredient">
                    <div class="input-group justify-content-center">
                        <div className="input-group-prepend">
                            <input type="text" 
                                placeholder='Input the Recipe Title'
                                autoComplete='Off' 
                                class="form-control" />
                            </div>


                        <div className="recipeIngredients"> 
                            <input type="text" 
                                placeholder="Input ingredients"
                                autoComplete='Off'
                                class="form-control" />

                            </div>



                            <div className="recipeSteps"> 
                            <input type="text" 
                                placeholder="GG"
                                autoComplete='Off'
                                class="form-control" />



                            </div>








                            <div>

                            <input type="submit" value="Send" class="btn btn-primary mb-2" />

                            /div></div>
                        </div>

                </form>

                {
                items.map(item => (
                    <div class="row padding">
                        <div class="alert alert-info rounded-pill" role="alert">
                            <i class="fa fa-user mr-2"></i> <i> Ingredient: {item.ingredients} </i>
                        </div>
                    </div>       
                ))
                }
            </div>
        </section>
    );
}

export default Ingredient;
